BEGIN TRANSACTION;
CREATE TABLE "category" (
	`id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`name`	TEXT NOT NULL,
	`type`	INTEGER NOT NULL DEFAULT 1
);
CREATE TABLE "category_i18n" (
	`category_id`	INTEGER NOT NULL,
	`language_code`	TEXT NOT NULL,
	`name`	TEXT NOT NULL,
	PRIMARY KEY(category_id,language_code)
);
CREATE TABLE "en_index" (
	`code`	TEXT NOT NULL UNIQUE,
	`letter`	TEXT NOT NULL UNIQUE,
	PRIMARY KEY(code)
);
CREATE TABLE "entry" (
	`id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE,
	`name`	TEXT NOT NULL,
	`img`	TEXT,
	`source_code`	TEXT NOT NULL,
	`en_index`	TEXT NOT NULL,
	`ja_index`	TEXT NOT NULL,
	`furigana`	TEXT,
	`glossary_order`	INTEGER NOT NULL
);
CREATE TABLE "entry_category" (
	`entry_id`	INTEGER NOT NULL,
	`category_id`	INTEGER NOT NULL,
	`ordering`	INTEGER NOT NULL DEFAULT 1
);
CREATE TABLE "entry_i18n" (
	`entry_id`	INTEGER NOT NULL,
	`language_code`	TEXT NOT NULL,
	`title`	TEXT NOT NULL,
	`content`	TEXT,
	`note`	TEXT,
	PRIMARY KEY(entry_id,language_code)
);
CREATE TABLE `glossary` (
	`id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE,
	`name`	TEXT NOT NULL
);
CREATE TABLE "glossary_i18n" (
	`glossary_id`	INTEGER NOT NULL,
	`language_code`	TEXT NOT NULL,
	`name`	TEXT NOT NULL,
	PRIMARY KEY(glossary_id,language_code)
);
CREATE TABLE "ja_index" (
	`code`	TEXT NOT NULL UNIQUE,
	`hiragana`	TEXT NOT NULL UNIQUE,
	`gojuuon`	TEXT NOT NULL,
	`gojuuon_code`	TEXT NOT NULL,
	PRIMARY KEY(code)
);
CREATE TABLE "language" (
	`code`	TEXT NOT NULL UNIQUE,
	PRIMARY KEY(code)
);
CREATE TABLE "source" (
	`code`	TEXT NOT NULL UNIQUE,
	`name`	TEXT NOT NULL,
	`release_date`	TEXT,
	`page`	TEXT,
	`glossary_id`	INTEGER,
	`translated`	INTEGER NOT NULL,
	PRIMARY KEY(code)
);
CREATE TABLE "source_i18n" (
	`source_code`	TEXT NOT NULL,
	`language_code`	TEXT NOT NULL,
	`name`	TEXT NOT NULL,
	`part`	TEXT,
	PRIMARY KEY(source_code,language_code)
);
DELETE FROM "sqlite_sequence";
COMMIT;
